    //             kho_giavi += 1;
    //         }
    //         if (product.category === "Rau-Củ-Trái cây") {
    //             rau_cu_traicay += 1;
    //         }
    //         if (product.category === "Đồ uống - Giải khát") {
    //             do_uong += 1;
    //         }
    //         if (product.category === "Bánh kẹo - Đồ ăn vặt") {
    //             banh_keo += 1;
    //         }
    //         if (product.category === "Thực phẩm chế biến") {
    //             che_bien += 1;
