                /div> <
                /div>

                <
                div className = "col-xl-3 col-sm-6 mb-3" >
                <
                div className = "card text-white bg-light o-hidden h-100" >
                <
                div className = "card-body" > { /* Doughnut Chart */ } <
                h6 className = 'text-dark' > Tình trạng số lượng hàng < /h6> <
                div className = "doughnutChart" >
                <
                Doughnut data = { doughnutState }
                /> <
                /div> <
                /div> <
                /div> <
                /div>

                <
                div className = "col-xl-3 col-sm-6 mb-3" >
                <
                div className = "card text-white bg-light o-hidden h-100" >
                <
                div className = "card-body" > { /* Doughnut Chart */ } <
                h6 className = 'text-dark' > Tình trạng đơn hàng < /h6> { /* Doughnut Chart */ } <
                div className = "doughnutChart" >
                <
                Doughnut data = { doughnutStateOrder }
                /> <
                /div> <
                /div> <
                /div> <
                /div>

                <
                div className = "col-xl-6 col-sm-12 mb-3" >
                <
                div className = "card text-white bg-light o-hidden h-100" >
                <
                div className = "card-body" >
                <
                h6 className = 'text-dark' > Tổng doanh thu < /h6> { /* Line chart */ } <
                div className = "lineChart" >
                <
                Line data = { lineState }
                /> <
                /div> <
                /div> <
                /div> <
                /div>

                <
                /div> <
                /Fragment>
            )
        }

        <
        div className = 'row' >

        <
        div className = 'col-md-6' >
        <
        ChartIncome / >
        <
        /div> <
        div className = 'radar col-md-5 card text-white bg-light' >

        <
        Radar data = { data }
        /> <
        /div> <
        /div>

        <
        /div> <
        /div> <
        /div> <
        /div>


        <
        /Fragment >
    )
